// destructuring to assign parameters 
function setCookie({name: name, expires: expires = '', samesite: samesite = '', secure: secure = false, maxAge: maxAge = '', domain: domain, path: path, httpOnly: httpOnly = false, value: value} = {}) {
    const Cookie = require('postman-collection').Cookie

    const argList = {
        'name': name,
        'expires': expires,
        'samesite': samesite,
        'secure': secure,
        'maxAge': maxAge,
        'domain': domain,
        'path': path,
        'httpOnly': httpOnly,
        'value': value
    }
    const cookieParamList = {}
    _.map(argList, (value, key) => { 
        if(value && typeof value !== "undefined") {
            cookieParamList[key] = value
        }
    })
    return new Cookie(cookieParamList)
}

function iso8601ToUnixTimestamp(isoDate) {
  var date = new Date(isoDate);
  var unixTimestamp = date.getTime() / 1000; // Divide by 1000 to convert milliseconds to seconds
  return unixTimestamp;
}

function expiresISO8601() {
    // Crea una data corrente
    var dataCorrente = new Date();

    // Ottieni i componenti della data
    var anno = dataCorrente.getFullYear();
    var mese = String(dataCorrente.getMonth() + 1).padStart(2, '0'); // Aggiungi 1 al mese poich i mesi sono indicizzati da zero
    var giorno = String(dataCorrente.getDate()).padStart(2, '0');
    var ore = String(dataCorrente.getHours()).padStart(2, '0');
    var minuti = String(dataCorrente.getMinutes()).padStart(2, '0');
    var secondi = String(dataCorrente.getSeconds()).padStart(2, '0');
    var millisecondi = String(dataCorrente.getMilliseconds()).padStart(3, '0');

    // Crea la stringa nel formato ISO 8601
    var dataISO8601 = `${anno}-${mese}-${giorno}T${ore}:${minuti}:${secondi}.${millisecondi}Z`;

    return dataISO8601;  // Esempio di output: "2023-06-30T14:50:25.000Z"
}

/**
 * Get the value of the desider cookie, given the cookie name
 * @param cookieName {string} desired cookie name
 * @return value of the desired cookie, if found
 */
function getCookieValue(cookieName) {
    const desiredCookie = cookieName

    const getHeaderList = (headerName) => 
        pm.response.headers
        .filter(header=>header.key.toLowerCase() === headerName.toLowerCase())
        .map(f=>f.value)

    // Map each key-value pair into an object
    const headerList = getHeaderList('set-cookie')
    const cookieList = headerList
        .map( header => header.replace(/\s/g, '').split(";"))
        .map(pairs => pairs.reduce( (result, pair) => {
        const keyValue = pair.split("=")
        const key = keyValue[0].trim()
        const value = keyValue[1]
        result[key] = value
        return result
    }, {}))

    const cookieFound = cookieList.find( dictionary => {
        return dictionary.hasOwnProperty(desiredCookie)
    })

    return cookieFound[desiredCookie]
}

function storeCookieIntoCollection(name, cookie) {
    pm.collectionVariables.set(name, JSON.stringify(cookie, null, 2))
}

/**
 * varList: array of strings
 * Returns array in collectionVariable JSON format
 */
function createCollectionVariableDictionary(varList) {
    const res = _.chain(variableList)
                .map((v, i) => {
                    const template = `{
                        "key": "#name#",
                        "value": "",
                        "enabled": true,
                        "sessionValue": "",
                        "sessionIndex": #index#
                    }`

                    return template.replace('#name#', v)
                                .replace('#index#', i)
                })
                .toArray()
                .join(',')
    return JSON.parse(`[${res}]`)
}
